import React, { useState } from 'react'
import Avatar from '@material-ui/core/Avatar'
import Button from '@material-ui/core/Button'
import CssBaseline from '@material-ui/core/CssBaseline'
import TextField from '@material-ui/core/TextField'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Checkbox from '@material-ui/core/Checkbox'
import { Link, Redirect } from "react-router-dom";
import Grid from '@material-ui/core/Grid'
import LockOutlinedIcon from '@material-ui/icons/LockOutlined'
import Typography from '@material-ui/core/Typography'
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Layout from '../../Components/Layout/Layout'

import { useDispatch, useSelector } from 'react-redux'


const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    }
}))

export default function SignIn() {

    const auth = useSelector(state => state.auth);
    const dirKey = localStorage.getItem('Dirkey');

    const classes = useStyles();

    const [info, setInfo] = useState("");
    const [password, setPass] = useState("");

    const dispatch = useDispatch();

    const signIn = (event) => {
        event.preventDefault();

        const email = info.includes("@") ? info : false;
        const contact = info.includes("@") ? false : info;

        const loginInfo = { email, contact, password };

        // console.log(bodyData);

        // try {
        //   const { data } = await signin({email,contact,password});
        //   console.log(data);
        // } catch (error) {
        //   console.log(error);
        // }
    }


    if (auth.authenticate) {
        return <Redirect to={'/'} />
    }

    return (
            <Container component='main' maxWidth='xs' style={{ minHeight: "500px" }}>
                <CssBaseline />
                <div className={classes.paper}>
                    <Avatar className={classes.avatar}>
                        <LockOutlinedIcon />
                    </Avatar>
                    <Typography component='h1' variant='h5'>
                        Sign in
                    </Typography>
                    {
                        auth.errorMessage ?
                            <div class="alert alert-danger" role="alert" style={{ width: "100%" }}>
                                Invalid login, please try again
                            </div>
                            : <span></span>
                    }
                    <form className={classes.form} noValidate onSubmit={signIn}>
                        <TextField
                            variant='outlined'
                            margin='normal'
                            required
                            fullWidth
                            id='email'
                            label='Email Address'
                            name='email'
                            autoComplete='email'
                            autoFocus
                            value={info}
                            onChange={(e) => setInfo(e.target.value)}
                        />
                        <TextField
                            variant='outlined'
                            margin='normal'
                            required
                            fullWidth
                            name='password'
                            label='Password'
                            type='password'
                            id='password'
                            autoComplete='current-password'
                            value={password}
                            onChange={(e) => setPass(e.target.value)}
                        />
                        <FormControlLabel
                            control={<Checkbox value='remember' color='primary' />}
                            label='Remember me'
                        />
                        <Button
                            type='submit'
                            fullWidth
                            variant='contained'
                            color='primary'
                            className={classes.submit}
                        >
                            Sign In
                        </Button>
                        <Grid container>
                            <Grid item xs>
                                <Link to='#' variant='body2'>
                                    Forgot password?
                                </Link>
                            </Grid>
                            <Grid item>
                                <Link to="/signup" variant='body2'>
                                    {"Don't have an account? Sign Up"}
                                </Link>
                            </Grid>
                        </Grid>
                    </form>
                </div>
                {/* <Box mt={8}>
        <Copyright />
      </Box> */}
            </Container>
    )
}
