import React from 'react';
import Layout from '../../Components/Layout/Layout';
import { Container, Row, Col } from 'react-bootstrap';
import { useSelector } from 'react-redux';

const Course = (props) => {
    const slug = props.match.params.slug;
    const courses = useSelector(state=>state.course.courses);
    const item = courses.find(x => x.slug === slug);
    return (
        <div>
            <Layout>
                <div className="mainDiv">
                    <Container>
                        <Row>
                            <Col>
                                <h2>{(item?.courseName).toUpperCase()}</h2>
                                <p className="text-justify">Attend the Model Test formatted in this course to prepare
                                     yourself for the upcoming medical admission exam and be confident</p>
                            </Col>
                            <Col>
                                <img  src = {item?.courseImage} alt="courseimage" height="350px" width="100%"/>
                            </Col>
                        </Row>
                    </Container>
                </div>
            </Layout>

        </div>
    );
};

export default Course;
