import React, { useState, useEffect, useRef } from 'react';
import Layout from '../../Components/Layout/Layout';
import { useSelector } from 'react-redux';
import axios from '../../helpers/axios';
import { Row, Col, Container, Button, Modal } from 'react-bootstrap';
import { BiTime } from 'react-icons/bi'
import { Link } from 'react-router-dom';



const Question = (props) => {
    const [modeltests, setModeltests] = useState({});
    const [questions, setQuestions] = useState({});
    const [error, setError] = useState("");
    const courses = useSelector(state => state.course.courses);
    const course = courses.find(x => x._id === modeltests?.courseId);
    const [hour, setHour] = useState(0);
    const [second, setSecond] = useState(0);
    const [minute, setMinute] = useState(0);
    const [answer, setAnswer] = useState([]);
    const [correctAnswer,setCorrectAnswer]=useState(0);
    const [wrongAnswer,setWrongAnswer]=useState(0);

    const [show, setShow] = useState(false);
    const handleShow = () => setShow(true);

    const d = new Date();
    useEffect(async () => {
        try {
            const res = await axios.get(`/get/model-tests/${props.match.params.slug}`);
            setModeltests(res.data.modeltest);
        } catch (err) {
            setError("Something wrong!");
        }
    }, []);
    useEffect(async () => {
        try {
            const res = await axios.get(`/get/questions/${props.match.params.slug}/${props.match.params.modelId}`);

            setQuestions(res.data.questions);
        } catch (err) {
            setError("Something wrong!");
        }
    }, []);
    const modeltest = modeltests?.modeltests?.find(x => x._id === props.match.params.modelId);

    function capitalize(str) {
        return str?.charAt(0).toUpperCase() + str?.slice(1);
    }
    let Interval = useRef();
    const startTimer = () => {
        const countDowntime = Date.now() + 5400000;
        Interval = setInterval(() => {
            const now = new Date();
            const distance = countDowntime - now;

            const hour = Math.floor(distance / (60 * 60 * 1000));

            const minute = Math.floor((distance % (60 * 60 * 1000)) / (60 * 1000));

            const s = Math.floor((distance % (60 * 1000)) / 1000);

            if (distance < 0) {

                clearInterval(Interval);
                handleShow();
            }
            else {
                setSecond(s);
                setMinute(minute);
                setHour(hour);
            }

        }, 1000)


    }
    useEffect(() => {
        startTimer();
        return () => {
            clearInterval(Interval.current);
        }
    }, [])

    const handleChange=(e,index)=>{
        setAnswer[index] = e.target.value;
        console.log(answer);

    }

    return (
        <Layout>
            <div className="mainDiv">
                <Container>
                    <h3 style={{ textAlign: 'center' }}>
                        {modeltest?.modelName ? capitalize(modeltest?.modelName) + ', ' : null}{course?.courseName ? capitalize(course?.courseName) : null}
                    </h3>
                    <Row >
                        <Col><p style={{ fontSize: '20px' }}><BiTime /> Time:{hour} : {minute} : {second}</p></Col>
                        <Col className="text-right"><p style={{ fontSize: '20px' }}>Total marks: 100</p></Col>
                    </Row>
                </Container>

                <Container>
                    <hr />
                    {
                        questions?.questions?.map((item, index) => {
                            return (
                                <div>
                                    <Row>
                                        <Col><p>{index + 1} {item.ques}</p></Col>
                                    </Row>
                                    <Row>
                                        <Col>
                                            <div className="form-check">
                                                <input className="form-check-input" type="radio" name={"radio-"+index}  onChange={handleChange(index)}/>
                                                <labe className="form-check-label">A. {item.optionA}</labe>

                                            </div>
                                        </Col>
                                        <Col>
                                            <div className="form-check">
                                                <input className="form-check-input" type="radio" name={"radio-"+index}  onChange={handleChange(index)}/>
                                                <labe className="form-check-label">B. {item.optionB}</labe>
                                            </div>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col>
                                            <div className="form-check">
                                                <input className="form-check-input" type="radio" name={"radio-"+index}  onChange={handleChange(index)}/>
                                                <labe className="form-check-label">C. {item.optionC}</labe>
                                            </div>
                                        </Col>
                                        <Col>
                                            <div className="form-check">
                                                <input className="form-check-input" type="radio" name={"radio-"+index}  onChange={handleChange(index)}/>
                                                <labe className="form-check-label">D. {item.optionD}</labe>
                                            </div>
                                        </Col>
                                    </Row>

                                </div>

                            )
                        })
                    }
                </Container>

            </div>
            <Modal show={show} style={{ marginTop: '200px' }}>
                <Modal.Body>Your exam has finished!</Modal.Body>
                <Modal.Footer>
                    <Link to={'/'}>
                        <Button variant="success" style={{ marginRight: '180px' }}>
                            View Result
                        </Button>
                    </Link>
                </Modal.Footer>
            </Modal>

        </Layout>

    );
};

export default Question;
