import { userConstants } from '../Actions/constants'

const initialState = {
    message: "",
    loading: false,
    authenticate: false,
    authenticating: false,
    token: null,
    user: {
        _id :"",
        fullname: "",
        email: "",
        contact: "",
        userImage: "",
        institutionName: "",
        gender: ""
    }
}
/*
const makeUpdate = (brands, b) => {
    const br = brands.filter(x => x._id !== b._id);
    br.push({
        _id: b._id,
        name: b.name,
        slug: b.slug,
        description: b.description,
        createdAt: b.createdAt,
        updatedAt: b.updatedAt,
        brandImage: b.hbrandImage
    })
    return br;

}
const makeUpdate2 = (brands, id) => {
    const b = brands.filter(x => x._id !== id);
    return b;

}
const makeUpdate3 = (brands, helmetbrand) => {

    brands.push({
        _id: helmetbrand._id,
        name: helmetbrand.name,
        slug: helmetbrand.slug,
        description: helmetbrand.description,
        createdAt: helmetbrand.createdAt,
        updatedAt: helmetbrand.updatedAt,
        hbrandImage: helmetbrand.hbrandImage
    });
    return brands;

}
*/
export default (state = initialState, action) => {
    console.log(action.type);
    switch (action.type) {
        case userConstants.USER_LOGIN_REQUEST:
            state = {
                ...state,
                loading: true,
                authenticating:true
            }
            break;
        case userConstants.USER_LOGIN_SUCCESS:
            state = {
                ...state,
                token : action.payload.token,
                user : action.payload.user,
                loading: false,
                authenticate:true,
                authenticating:false
            }
            break;
        case userConstants.USER_LOGIN_FAILURE:
            state = {
                ...state,
                loading:false,
                authenticating:false,
                message : action.payload.message
            }
            break;
        
    }
    return state;
}
